import React from "react";
const Home = props => {
    return(
        <>
            <h2>{props.tituloPagina}</h2>
            {props.dados.map((item, i) => (
                <div className="card">
                    <div className="thumb">
                        <img src={props.dados[i].img} alt=""/>
                    </div>
                    <div className="detalhes">
                        <h4>{props.dados[i].isbn}</h4>
                        <h3>{props.dados[i].titulo}</h3>
                        <h4>{props.dados[i].autor}</h4>
                        <p>{props.dados[i].desc}</p>
                        <a href="#">leia mais &gt;</a>
                    </div>
                </div>

            ))}            
        </>
    );
};

export default Home;
